/*Application of hierarchies and derivation of combinations for the given input dataset*/create function APPLY_HIERARCHIES_AND_AGGREGATION(INPT_CB) {
returns 
hierarchiesAndAggregation(INPT_CB)
as dataset
}

/*Returns a cube that is the result of the application of mappings in reverse order*/create function APPLY_INVERSE_MAPPINGS(INPT_CB) {
returns 
inverseMap(INPT_CB)
as dataset
}

/**/define procedure CHCK_AC_RPRTNG_RQRMNTS(input LNS_E as dataset, input INSTRMNTS_CSTMRS_E as dataset, input INSTRMNTS_CRDTRS_E as dataset, input CNTRPRTS_E as dataset, input VARIABLE_NAME as string, output RSLT as dataset) {
/*A set containing all Instrument unique identifiers (INSTRMNT_UNQ_ID)*/ALL_LNS := LNS_E [keep(INSTRMNT_UNQ_ID)];

/*Extraction of instruments, servicers*/INSTRMNTS_SRVCRS_AC := INSTRMNTS_SRVCRS_E [filter(CNTRPRTY_ID in (ENTTS_LGL_ENTTY_MMBR_STT))];
/*Extract all serviced instruments*/INSTRMNTS_SRVCRS_INSTRMNTS_AC := INSTRMNTS_SRVCRS_AC [keep(INSTRMNT_UNQ_ID)];
/*For serviced instruments we add the debtor of these instruments*/SRVD_INSTRMNTS_MMB_STT := [INSTRMNTS_SRVCRS_INSTRMNTS_AC, INSTRMNTS_CSTMRS_E on INSTRMNT_UNQ_ID];
/*see AnaCredit regulation, Article 4, 1.(a) (iv) (i)*/SRVD_INTRNL_INSTRMNTS_AC := SRVD_INSTRMNTS_MMB_STT [filter(CNTRPRTY_ID in (ENTTS_LGL_ENTTY))];
/*A set of instruments that meet the requirements described in the AnaCredit regulation, Article 4, 1.(a) (iv) (i)*/SRVD_INTRNL_INSTRMNTS_AC := SRVD_INTRNL_INSTRMNTS_AC [keep(INSTRMNT_UNQ_ID)];
/*Joining with creditors */SRVD_INSTRMNTS_NT_HLD_BY_CI := [SRVD_INSTRMNTS_MMB_STT, INSTRMNTS_CRDTRS_E on INSTRMNT_UNQ_ID] {keep(A.ALL_VARIABLES, B.CNTRPRTY_ID as "CRDTR_ID")};
/*Identification of serviced instruments where the creditor is not a credit institution and resident in a reporting Member state, see AnaCredit regulation, Article 4, 1.(a) (iv) (ii)*/SRVD_INSTRMNTS_NT_HLD_BY_CI := SRVD_INSTRMNTS_NT_HLD_BY_CI [filter(INSTTTNL_SCTR not in ("S122_A") and CNTRY in (ANCRDT_CNTRS_OTHR))];
/*A set of instruments that meet the requirements described in the AnaCredit regulation, Article 4, 1.(a) (iv) (ii)*/SRVD_INSTRMNTS_NT_HLD_BY_CI := SRVD_INSTRMNTS_NT_HLD_BY_CI [keep(INSTRMNT_UNQ_ID)];
/*A set of Instrument unique identifiers (INSTRMNT_UNQ_ID) of instruments that meet the criteria described in the AnaCredit regulation, Article 4, 1.(a) (iv)*/SRVD_INSTRMNTS_AC := union(SRVD_INTRNL_INSTRMNTS_AC, SRVD_INSTRMNTS_NT_HLD_BY_CI);

/*Extraction of instruments, debtors*/INSTRMNTS_DBTRS_AC := INSTRMNTS_CSTMRS_E;
/*Enrichment with (related) instrument information*/INSTRMNTS_DBTRS_AC := [INSTRMNTS_DBTRS_AC, LNS_E on INSTRMNT_UNQ_ID];
/*Enrichment with (related) counterparty information*/INSTRMNTS_DBTRS_AC := [INSTRMNTS_DBTRS_AC, CNTRPRS_E on CNTRPRTY_ID];
/*Filter for eligible counterparties*/INSTRMNTS_DBTRS_AC := INSTRMNTS_DBTRS_AC [filter(IS_ELGBL_CNTRPRTY = "T")];
/*Filter for instruments that are (in general) eligible for AnaCredit*/INSTRMNTS_DBTRS_AC := INSTRMNTS_DBTRS_AC [filter(TYP_INSTRMNT in (INSTRMNTS_ELGBL_AC))];
/*Create dataset comprising amounts per counterparty*/AMNTS_PR_CNTRPRTY := INSTRMNTS_DBTRS_AC [keep(CNTRPRTY_ID, TYP_INSTRMNT, OFF_BLNC_SHT_AMNT, OTSTNDNG_NMNL_AMNT, ACCMLTD_WRTFFS)];
/*Add the Off-balance sheet amount (OFF_BLNC_SHT_AMNT) and the Outstanding nominal amount (OTSTNDNG_NMNL_AMNT)*/AMNTS_PR_CNTRPRTY := AMNTS_PR_CNTRPRTY [calc (OFF_BLNC_SHT_AMNT + OTSTNDNG_NMNL_AMNT) as "ACCMLTD_AMNT"];
/*Apply threshold*/AMNTS_PR_CNTRPRTY := AMNTS_PR_CNTRPRTY [filter(ACCMLTD_AMNT >= THRSHLD_AC)];
/*Connect the Amounts per counterparty (AMNTS_PR_CNTRPRTY) with the instruments in order to identifiy those instruments that meet the criteria described in the AnaCredit regulation, Article 4, 1.(a) (i)-(iii)*/INSTRMNTS_DBTRS_AC := [INSTRMNTS_DBTRS_AC, AMNTS_PR_CNTRPRTY on CNTRPRTY_ID];
/*A set of Instrument unique identifiers (INSTRMNT_UNQ_ID) of instruments that meet the criteria described in the AnaCredit regulation, Article 4, 1.(a) (i)-(iii)*/INSTRMNTS_DBTRS_AC := INSTRMNTS_DBTRS_AC[keep(INSTRMNT_UNQ_ID)];

/*A set of Instrument unique identifiers (INSTRMNT_UNQ_ID) of instruments that meet the criteria described in the AnaCredit regulation, Article 4, 1.(a)*/INSTRMNTS_TBR_AC := union(SRVD_INSTRMNTS_AC, INSTRMNTS_DBTRS_AC);
/*Identification of all instruments that do not need to be reported w.r.t. AnaCredit*/INSTRMNTS_NTBR_AC := setdiff(ALL_LNS, INSTRMNTS_TBR_AC);
/*Set the variable VARIABLE_NAME (see input of this procedure) to 'False (F)'*/INSTRMNTS_NTBR_AC := INSTRMNTS_NTBR_AC [calc "F" as "VARIABLE_NAME"];
/*Set the variable VARIABLE_NAME (see input of this procedure) to 'True (T)'*/INSTRMNTS_TBR_AC := INSTRMNTS_TBR_AC [calc "T" as "VARIABLE_NAME"];

RSLT := union(INSTRMNTS_TBR_AC, INSTRMNTS_NTBR_AC);
}

create function D_ACCMLTD_IMPRMN_NGTV_CHNG_FV_CR(ACCMLTD_IMPRMNT, ACCMLTD_NGTV_CHNG_FV_CR, PRVSNS) {
returns 
(ACCMLTD_IMPRMNT +ACCMLTD_NGTV_CHNG_FV_CR + PRVSNS)
as integer
}

create function D_ACCMLTD_NGTV_CHNGS_FV_CR (ACCMLTD_CHNGS_FV_CR) {
returns 
if (ACCMLTD_CHNGS_FV_CR < 0) then ACCMLTD_CHNGS_FV_CR else 0
as integer
}

create function D_ACCMLTD_NGTV_CHNGS_FV_CR_NN_PRFMNG_EXPSRS(PRFRMNG_STTS, ACCMLTD_CHNGS_FV_CR) {
returns 
if (PRFRMNG_STTS in ("1", "12", "13") and ACCMLTD_CHNGS_FV_CR < 0) then ACCMLTD_CHNGS_FV_CR else 0
as integer
}

create function D_ACCMLTD_PRTL_WRTFFS(ACCMLTD_WRTFFS, CRRYNG_AMNT, ACCMLTD_IMPRMNT) {
returns 
if (ACCMLTD_WRTFFS > 0 and (CRRYNG_AMNT + ACCMLTD_IMPRMNT) > 0) then ACCMLTD_WRTFFS
else 0
as integer
}

create function D_ACCMLTD_TTL_WRTFFS(ACCMLTD_WRTFFS, CRRYNG_AMNT, ACCMLTD_IMPRMNT) {
returns 
if (ACCMLTD_WRTFFS > 0 and (CRRYNG_AMNT + ACCMLTD_IMPRMNT) = 0) then ACCMLTD_WRTFFS
else 0
as integer
}

create function D_ACCNTNG_CLSSFCTN_OTHR_FNNCL_LBLTS_FINREP(OFF_BLNC_SHT_ACCNTNG_CLSSFCTN) {
returns 
if (OFF_BLNC_SHT_ACCNTNG_CLSSFCTN in ("92")) then "392" 
else "21"
as string
}

/*map: (Accumulated impairment, Credit quality step, Currency denomination of instruments, Issue date, Maturity date, Reference date, ECAI/ECA, Exposure class, Exposure value, External credit assessment, International organisations, Is regional government or local authority treated as central government, Is public sector entity treated as central government, Is equity with 250% risk weight, Is equity holding with 370% risk weight, Is public sector entity treated as regional government or local authority, Is Member State, Is qualifying holding with 1250% risk weight, Is re-securitisation, Is short-term credit assessment, Is same institutional protection scheme, Relationship with securitisation or credit transfer, Specific risk weight) &rArr; Credit quality step in BIRD process*/
create function D_ASSGN_CRDT_QLTY_STP_BIRD (ACCMLTD_IMPRMNT, CNTRY_CRRNCY, CRDT_QLTY_STP, CRDT_QLTY_STP_CNTRY, CRRNCY_CNTRY, CRRNCY_DNMNTN, CRRNCY_ISSR, CRRNCY_OWNR, DT_ISS, DT_MTRTY, DT_RFRNC, ECAI_ECA, ECAI_ECA_CNTRY, EXPSR_CLSS, EXPSR_VL, EXTRNL_CRDT_ASSSSMNT, EXTRNL_CRDT_ASSSSMNT_CNTRY, IMPRMNT_STTS, INTRNTNL_ORGNSTN, IS_CNTRL_GVRNMNT_TRTD_LG, IS_CNTRL_GVRNMNT_TRTD_PS, IS_EQTY_250_RSK_WGHT, IS_EQTY_HLD_370_RSK_WGHT, IS_LCL_GVRNMNT_TRTD_PS, IS_MMBR_STT, IS_MMBR_STT_OWNR, IS_QLFY_HLD_1250_RSK_WGHT, IS_RSCRTSTN, IS_RSCRTSTN_ORGNL, IS_SHRT_TRM_CRDT_ASSSSMNT, IS_SM_PRTCTN_SCHM, RLTNSHP_SCRTSTN_CRDT_TRNSFR, SPCFC_RSK_WGHT) {
returns 
 /*Exposures to central governments or central banks*/
 if (EXPSR_CLSS = "6") then  
 (
  /*exposures to ECB have 0% risk weight (art. 114(3) CRR)*/
  if INTRNTNL_ORGNSTN = "4F" then "81" 
  /*exposures to Member States' central governments, and central banks denominated in the domestic currency of that central government and central bank shall be assigned a risk weight of 0 % if that currency is the domestic currency of the owner (in this case it is assumed that it is “funded”) (art. 114(4) CRR)*/
  elseif (IS_MMBR_STT = "T" and IS_MMBR_STT_OWNR = "T" and CRRNCY_DNMNTN = CRRNCY_ISSR and CRRNCY_DNMNTN = CRRNCY_OWNR) then "81" 
  /*specific risk weights are applied*/
  elseif (not isnull (SPCFC_RSK_WGHT)) then "99" 
  /*assign the credit quality step if a credit assessment by an ECAI or an ECA is available (art. 114(2) and 137(2) CRR)*/
  elseif (not isnull (EXTRNL_CRDT_ASSSSMNT) and ECAI_ECA = "1") then CRDT_QLTY_STP 
  elseif (not isnull (EXTRNL_CRDT_ASSSSMNT) and ECAI_ECA = "2") then CRDT_QLTY_STP + 10 
  /*if a credit assessment is not available for the security, the country’s one is taken*/
  elseif (not isnull (EXTRNL_CRDT_ASSSSMNT_CNTRY) and ECAI_ECA_CNTRY = "1") then CRDT_QLTY_STP_CNTRY 
  elseif (not isnull (EXTRNL_CRDT_ASSSSMNT_CNTRY) and ECAI_ECA_CNTRY = "2") then CRDT_QLTY_STP_CNTRY + 10 
  /*if a credit assessment is not available then a 100% risk weight is assigned (art. 114(1) CRR)*/
  else "87" 
 ) 
 /*Exposures to institutions without a short-term credit assessment*/
 elseif (EXPSR_CLSS = "9") then
 (
  /*investments in equity attracting a 250% risk weight*/
  if IS_EQTY_250_RSK_WGHT = "T" then "92" 
  /*in the case referred to in Article 113(7) CRR a risk weight of 0% is assigned*/
  elseif IS_SM_PRTCTN_SCHM = "T" then "81" 
  /*specific risk weights are applied*/
  elseif not isnull (SPCFC_RSK_WGHT) then "99" 
  /*exposures to Member States’ institutions of a residual maturity of three months or less denominated in the domestic currency of that institution shall be assigned a risk weight of 20% if that currency is the domestic currency of the owner (in this case it is assumed that it is “funded”) (art. 119(2) referring to art. 114(4) CRR)*/
  elseif (D_NMBR_DYS(DT_RFRNC, DT_MTRTY)) > 0 and D_NMBR_DYS(DT_RFRNC, DT_MTRTY) > 90 and CRRNCY_DNMNTN = CRRNCY_ISSR and CRRNCY_DNMNTN = CRRNCY_OWNR and IS_MMBR_STT = "T" and IS_MMBR_STT_OWNR = "T") then "83" 
  /*assign the credit quality step if a credit assessment by an ECAI is available (art. 120 CRR)*/
  elseif not isnull (EXTRNL_CRDT_ASSSSMNT) then 
  (if D_NMBR_DYS(DT_RFRNC, DT_MTRTY) > 0 and D_NMBR_DYS(DT_RFRNC, DT_MTRTY) < 90 then CRDT_QLTY_STP + 10 
  else CRDT_QLTY_STP)
  /*if a credit assessment by an ECAI is not available and the original maturity is ≤ 3 months, the risk weight shall be 20% (art. 121(3) CRR)*/  
  elseif isnull (EXTRNL_CRDT_ASSSSMNT) and not isnull(EXTRNL_CRDT_ASSSSMNT_CNTRY) then 
   (if ECAI_ECA = "1" then CRDT_QLTY_STP_CNTRY + 20 
   /*if the central government is unrated, the risk weight is 100% (art. 121(2) CRR)*/
   else "87")
  else null
 )
 /*Exposures to regional governments or local authorities*/
 elseif (EXPSR_CLSS = "13") then 
 (
  /*specific risk weights are applied*/
  if not isnull (SPCFC_RSK_WGHT) then "99" 
  /*if the exposure is to be treated as exposure to the central government (art. 115(2) CRR) then the related risk weight is applied*/
  elseif IS_CNTRL_GVRNMNT_TRTD_LG = "T" then D_EXPSR_CNTR_GVRNMNTS1 (IS_MMBR_STT, CRRNCY_DNMNTN, CRRNCY_CNTRY, CRRNCY_OWNR, EXTRNL_CRDT_ASSSSMNT, ECAI_ECA, CRDT_QLTY_STP, EXTRNL_CRDT_ASSSSMNT_CNTRY, ECAI_ECA_CNTRY, CRDT_QLTY_STP_CNTRY) 
  /*exposures to Member States' regional governments or local authorities denominated in the domestic currency of that regional government or local authority shall be assigned a risk weight of 20 % if that currency is the domestic currency of the owner (in this case it is assumed that it is “funded”) (art. 115(5) CRR)*/
  elseif (IS_MMBR_STT = "T" and IS_MMBR_STT_OWNR = "T" and CRRNCY_DNMNTN = CRRNCY_CNTRY and CRRNCY_DNMNTN = CRRNCY_OWNR) then "83" 
  /*in the other cases they are risk-weighted as exposures to institutions, except for the preferential treatment for short-term exposures (art. 115(1) CRR)*/
  elseif not isnull(EXTRNL_CRDT_ASSSSMNT_CNTRY) then CRDT_QLTY_STP_CNTRY 
  elseif D_NMBR_DYS(DT_ISS, DT_MTRTY) <= 90 then "83" 
  elseif not isnull(EXTRNL_CRDT_ASSSSMNT) then CRDT_QLTY_STP + 20 
  else "87" 
 )
 /*Exposures to public sector entities*/
 elseif (EXPSR_CLSS = "12") then 
 (
  /*specific risk weights are applied*/
  if not isnull (SPCFC_RSK_WGHT) then "99" 
  /*if the exposure is to be treated as exposure to the central government (art. 116(4) CRR) then the related risk weight is applied*/
  elseif IS_CNTRL_GVRNMNT_TRTD_PS = "T" then D_EXPSR_CNTR_GVRNMNTS1 (IS_MMBR_STT, CRRNCY_DNMNTN, CRRNCY_CNTRY, CRRNCY_OWNR, EXTRNL_CRDT_ASSSSMNT, ECAI_ECA, CRDT_QLTY_STP, EXTRNL_CRDT_ASSSSMNT_CNTRY, ECAI_ECA_CNTRY, CRDT_QLTY_STP_CNTRY) 
  /*if the original maturity is ≤ 3 months, the risk weight shall be 20% (art. 116(3) CRR)*/
  elseif D_IS_LE_3MNTH(DT_RFRNC, DT_MTRTY) then "83" 
  /*if the exposure is to be treated as exposure to the regional government or local authority (art. 116(4) CRR) then the related risk weight is applied*/
  elseif (IS_LCL_GVRNMNT_TRTD_PS = "T") then 
  (
   if (IS_MMBR_STT = "T" and IS_MMBR_STT_OWNR = "T" and CRRNCY_DNMNTN = CRRNCY_OWNR and CRRNCY_DNMNTN = CRRNCY_CNTRY) then "83" 
   elseif not isnull (EXTRNL_CRDT_ASSSSMNT_CNTRY) then CRDT_QLTY_STP_CNTRY + 40 
   elseif not isnull (EXTRNL_CRDT_ASSSSMNT) then CRDT_QLTY_STP + 50 
   else "87"
  )
  /*assign the credit quality step if a credit assessment by an ECAI is available (art. 116(2) CRR)*/
  elseif not isnull (EXTRNL_CRDT_ASSSSMNT) then CRDT_QLTY_STP 
  /*if a credit assessment by an ECAI is not available, the credit assessment assigned to the central government of the jurisdiction in which the public sector entity is incorporated is used (art. 116(1) CRR)*/
  elseif not isnull (EXTRNL_CRDT_ASSSSMNT_CNTRY) then CRDT_QLTY_STP_CNTRY + 20 
  /*if the central government is unrated, the risk weight is 100% (art. 116(1) CRR)*/
  else "87"
 )
 /*Exposures to multilateral development banks*/
 elseif (EXPSR_CLSS = "11") then 
 (
  /*if the MDB is referred to in art. 117(2) CRR, the risk weight is 0%*/
  if INTRNTNL_ORGNSTN in ("1E", "1M", "5C", "5E", "5D", "7E", "5H", "5L", "5F", "4C", "4G", "1N", "7L", "7B") then "81" 
  /*otherwise they are risk-weighted as exposures to institutions, except for the preferential treatment for short-term exposures (art. 117(1) CRR)*/
  elseif not isnull (EXTRNL_CRDT_ASSSSMNT) then CRDT_QLTY_STP 
  elseif not isnull (EXTRNL_CRDT_ASSSSMNT_CNTRY) then CRDT_QLTY_STP_CNTRY + 20 
  else "87" 
 )
 /*Exposures to international organisations, 0% risk weight is assigned (art. 118 CRR)*/
 elseif (EXPSR_CLSS = "10") then "81"
 /*Exposures to corporates without a short-term credit assessment*/
 elseif (EXPSR_CLSS = "7") then 
 (
  /*assign the credit quality step if a credit assessment by an ECAI is available (art. 122(1) CRR)*/
  if not isnull (EXTRNL_CRDT_ASSSSMNT) then CRDT_QLTY_STP 
  /*if a credit assessment is not available the risk weight of exposures to the central government of the jurisdiction in which the corporate is incorporated is calculated*/
  elseif not isnull(EXTRNL_CRDT_ASSSSMNT_CNTRY) and ECAI_ECA_CNTRY = "1" then 
   (
   /*the risk weight above calculated is applied if it is higher than 100% (art. 122(2) CRR)*/
   if D_CLCLTN_RSK_WGHT("7", CRDT_QLTY_STP) = 150 then "88" 
   /*in all the other cases the risk weight is 100% (art. 122(2) CRR)*/
   else "87")
  else "87" 
 )
 /*Exposures to institutions and corporates with a short-term credit assessment*/
 elseif (EXPSR_CLSS = "8") then 
 (
  /*assign the credit quality step corresponding to the credit assessment (art. 131 CRR)*/
  if not isnull (EXTRNL_CRDT_ASSSSMNT) then CRDT_QLTY_STP 
  else null
 )
 /*Exposures in the form of units or shares in CIUs*/
 elseif (EXPSR_CLSS = "4") then
 (
  /*specific risk weights are applied*/
  if not isnull (SPCFC_RSK_WGHT) then "99" 
  /*assign the credit quality step if a credit assessment by an ECAI is available (art. 132(2) CRR)*/
  elseif not isnull (EXTRNL_CRDT_ASSSSMNT) then CRDT_QLTY_STP 
  /*if a credit assessment is not available then a 100% risk weight is assigned (art. 132(1) CRR)*/
  else "87"  
 )
 /*Exposures in the form of covered bonds*/
 elseif (EXPSR_CLSS = "3") then 
 (
  /*assign the credit quality step if a credit assessment by an ECAI is available for covered bonds (art. 129(4) CRR) or for the institution which issues them (art. 129(5) CRR)*/
  if not isnull(EXTRNL_CRDT_ASSSSMNT) then CRDT_QLTY_STP 
  /*if a credit assessment by an ECAI is not available, the risk weight of the institution which issues them is applied, which is assigned in accordance with the credit quality step of the central government of the jurisdiction in which the institution is incorporated (art. 129(5) and 121(1) CRR)*/
  elseif not isnull(EXTRNL_CRDT_ASSSSMNT_CNTRY) then CRDT_QLTY_STP_CNTRY + 20 
  /*if a credit assessment is not available then a 100% risk weight is assigned (art. 129(5) and 121(2) CRR)*/
  else "87" 
 )
 /*Exposures in default*/
 elseif (EXPSR_CLSS = "2") then
 (
  /*calculate specific credit risk adjustments*/
  if (INPRMNT_STTS in ("25", "26")) then 
   (
   /*assign a 150% or 100 % risk weight (art. 127 CRR)*/
   if (D_X_OVER_X_PLUS_Y(ACCMLTD_IMPRMNT, EXPSR_VL) * 100 < 20) then "88" 
   else "87"
   )
  else
   (
   /*assign a 150% or 100 % risk weight (art. 127 CRR)*/
   if (D_X_OVER_X_PLUS_Y(0, EXPSR_VL) * 100 < 20) then "88" 
   else "87"
   )
 )
 /*Items associated with particular high risk, a 150% risk weight is assigned (art. 128(1) CRR)*/
 elseif (EXPSR_CLSS = "14") then "88"
 /*Equity exposures*/
 elseif (EXPSR_CLSS = "1") then 
 ( 
  /*qualifying holdings with a 1250% risk weight*/
  if IS_QLFY_HLD_1250_RSK_WGHT = "T" then "90" 
  /*equity holdings with a 370% risk weight*/
  elseif IS_EQTY_HLD_370_RSK_WGHT = "T" then 93 
  /*investments in equity attracting a 250% risk weight*/
  elseif IS_EQTY_250_RSK_WGHT = "T" then 92 
  /*in all the other cases the risk weight is 100% (art. 133(2) CRR)*/
  else "87"  
 )
 /*Items representing securitisation positions*/
 elseif EXPSR_CLSS = "15" then
 ( 
  /*a risk weight is assigned after identifying re-securitisation positions and positions with a short term credit assessment (art. 251 CRR)*/

  if RLTNSHP_SCRTSTN_CRDT_TRNSFR = "2" then
   (
    if (IS_RSCRTSTN <> "T" and not isnull (EXTRNL_CRDT_ASSSSMNT)) then 
    (
     if (IS_SHRT_TRM_CRDT_ASSSSMNT = "T") then CRDT_QLTY_STP + 10 
     else CRDT_QLTY_STP 
    )
    elseif (IS_RSCRTSTN = "T" and not isnull (EXTRNL_CRDT_ASSSSMNT)) then
    (
     if (IS_SHRT_TRM_CRDT_ASSSSMNT = "T") then CRDT_QLTY_STP + 30 
     else CRDT_QLTY_STP + 20 
    )
    else "90" 
   )
  elseif RLTNSHP_SCRTSTN_CRDT_TRNSFR = "4" then
   (
    if (IS_RSCRTSTN_ORGNL <> "T" and not isnull (EXTRNL_CRDT_ASSSSMNT)) then 
    (
     if (IS_SHRT_TRM_CRDT_ASSSSMNT = "T") then CRDT_QLTY_STP + 10 
     else CRDT_QLTY_STP 
    )
    elseif (IS_RSCRTSTN_ORGNL = "T" and not isnull (EXTRNL_CRDT_ASSSSMNT)) then
    (
     if (IS_SHRT_TRM_CRDT_ASSSSMNT = "T") then CRDT_QLTY_STP + 30 
     else CRDT_QLTY_STP + 20 
    )
    else "90" 
   )
  else "90"   
 )
 /*Other items*/
 elseif (EXPSR_CLSS = "16") then "87"
 else null 
as string
}

/*map: (Is exposure class applicable, Exposure class (input), Relationship with securitisation or credit transfer, Type of risk transfer, Is equity with 250% risk weight, Institutional sector, Credit quality status, International organisations, Security rank level, LEI code, Type of instrument, Is particular high risk, Is covered bond, Characteristics for credit risk, Is short-term credit assessment ) &rArr; Exposure class*/
create function D_ASSGN_EXPSR_CLSS1 (IS_EXPSR_CLSS_APPLCBL, EXPSR_CLSS, RLTNSHP_SCRTSTN_CRDT_TRNSFR, TYP_RSK_TRNSFR, TYP_RSK_TRNSFR_ORGNL, IS_EQTY_250_RSK_WGHT, INSTTTNL_SCTR, CHRCTRSTCS_CRDT_RSK, IS_PRTCLR_HGH_RSK, TYP_INSTRMNT, SCRTY_RNK_LVL, CRDT_QLTY_STTS, INTRNTNL_ORGNSTN, LEI, IS_CVRD_BND, IS_SHRT_TRM_CRDT_ASSSSMNT) {
returns 
if IS_EXPRSR_CLSS_APPLCBL = "T" then 
(/*for exposures for which the IRB approach is followed the exposure class and the risk weight are equal to the variables provided in the input layer*/
if APPRCH_PRDNTL_PRPSS not in ("66", "67") then EXPSR_CLSS 
/*The exposures to a securitisation are classified as “items representing securitisation positions”*/
elseif (RLTNSHP_SCRTSTN_CRDT_TRNSFR = "2" and TYP_RSK_TRNSFR in ("1", "2")) then "15" 
elseif (RLTNSHP_SCRTSTN_CRDT_TRNSFR = "4" and TYP_RSK_TRNSFR_ORGNL in ("1", "2")) then "15" 
/*investments in equity attracting a 250% risk weight are classified as “exposures to institutions”*/
elseif (IS_EQTY_250_RSK_WGHT = "T" and INSTTTNL_SCTR in ("S122_A", "S122_B1", "S122_B2", "S125_B", "S125_C", "S125_D", "S125_E", "S125_I", "S126", "S127") and CHRCTRSTCS_CRDT_RSK = "1") then "9" 
/*items associated with particular high risk are classified in the specific exposure class*/
elseif IS_PRTCLR_HGH_RSK = "T" then "14"
/*equity and subordinated debt securities are classified as “equity exposures”*/
elseif TYP_INSTRMNT in ("220", "230") or (TYP_INSTRMNT = "210" and SCRTY_RNK_LVL in ("1", "2", "3", "5", "6")) then "1"
/*if the issuer is defaulted then the exposure is classified in default*/
elseif CRDT_QLTY_STTS in ("18", "19", "20") then "2"
/*if the issuer is an international organisation listed in Article 118 CRR, the exposure is classified in the specific class*/
elseif (INTRNTNL_ORGNSTN in ("1C", "5B", "4S", "4D", "4J1", "4J2", "4J3", "4J4", "4J5", "4J6", "4J7", "4J8") or LEI = "222100OW6UHQXNHKN143") then "10" 
/*if the issuer is a multilateral development bank listed in Article 117 CRR, the exposure is classified in the specific class*/
elseif INTRNTNL_ORGNSTN in ("5G", "7G", "5T", "5U", "1E", "1M", "5C", "5E", "5D", "7E", "5H", "5L", "5F", "4C", "4G", "1N", "7L", "7B") then "11" 
/*if the issuer’s sector is central bank or it is central government neither to be considered as public sector entity nor corporate, the exposure is classified as to central governments or central bank*/
elseif (INSTTTNL_SCTR = "S121" or (INSTTTNL_SCTR = "S1311" and CHRCTRSTCS_CRDT_RSK = "0")) then "6" 
/*if the issuer’s sector is state or local government neither to be considered as public sector entity nor corporate, the exposure is classified as to regional governments or local authorities*/
elseif (INSTTTNL_SCTR in ("S1312", "S1313") and CHRCTRSTCS_CRDT_RSK = "0") then "13" 
/*if the issuer’s sector is social security funds or it is credit institution, central, state or local government to be considered as public sector entity, the exposure is classified as to public sector entities*/
elseif (INSTTTNL_SCTR = "S1314" or (INSTTTNL_SCTR in ("S122_A", "S1311", "S1312", "S1313") and CHRCTRSTCS_CRDT_RSK = "2")) then "12" 
/*if the issuer’s sector is financial corporations, except some subsectors, and the entity is supervised, the exposure is classified as to institutions*/
elseif (INSTTTNL_SCTR = "S122_A" and CHRCTRSTCS_CRDT_RSK = "1") then 
   (if IS_CVRD_BND = "T" then "3"
   elseif IS_SHRT_TRM_CRDT_ASSSSMNT = "T" then "8"
   else "9")
elseif INSTTTNL_SCTR in ("S122_B1", "S122_B2", "S125_B", "S125_C", "S125_D", "S125_E", "S125_I", "S126", "S127") then 
   (if not isnull(EXTRNL_CRDT_ASSSSMNT) and IS_SHRT_TRM_CRDT_ASSSSMNT = "T" then "8" 
   else "9")
/*if the issuer’s sector is non-financial corporations, some subsectors of financial corporations, households or non-profit institutions serving households or it is financial corporations, except some subsectors, and the entity is not supervised or it is central, state or local government and the entity is considered as corporate, the exposure is classified as to corporates*/
elseif (INSTTTNL_SCTR in ("S11", "S125_A", "S128", "S129", "S14_A", "S14_B", "S15") or (INSTTTNL_SCTR in ("S122_A", "S122_B1", "S122_B2", "S125_B", "S125_C", "S125_D", "S125_E", "S125_I", "S126", "S127") and CHRCTRSTCS_CRDT_RSK = "0") or (INSTTTNL_SCTR in ("S1311", "S1312", "S1313") and CHRCTRSTCS_CRDT_RSK = "3")) then 
   (if (not isnull (EXTRNL_CRDT_ASSSSMNT) and IS_SHRT_TRM_CRDT_ASSSSMNT = "T") then "8" 
   else "7") 
/*if the issuer’s sector is MMFs or non-MMF investment funds, the exposure is classified as in the form of units or shares in CIUs*/
elseif INSTTTNL_SCTR in ("S123", "S124") then "4" 
/*in case none of the above conditions are met the exposure is classified as “other items” (in practice it should never happen)*/
else "16") 
/*in any other case the exposure class is not applicable*/else "0" 
as string
}

