/*Input data set structure {Identifier, Reference date, Value,...}*/define procedure DELTA_VALUE(input INPT as dataset, input ID_NAME as string, input DATE_NAME as string, input VALUE_NAME as string, input T0 as date, input T1 as date, output OTPT as dataset) {
       TMP_DS := INPT [keep(ID_NAME, DATE_NAME, VALUE_NAME)];
       DS_T0 := TMP_DS [filter(DATE_NAME = T0)];
       DS_T1 := TMP_DS [filter(DATE_NAME = T1)];
       TMP_DS := TMP_DS[keep(ID_NAME)];
       OTPT := [left TMP_DS as "A", DS_T0 as "B" on ID_NAME] {keep(A.ID_NAME, B.VALUE_NAME)};
       OTPT := OTPT [rename VALUE_NAME as "VALUE_NAME_T0"];
       OTPT := [left TMP_DS as "A", DS_T1 as "B" on ID_NAME] {keep(A.ID_NAME, B.VALUE_NAME)};
       OTPT := OTPT [rename VALUE_NAME as "VALUE_NAME_T1"];
       OTPT := OTPT [calc (VALUE_NAME_T1 - VALUE_NAME_T0) as "CHNG_FV"];
       OTPT := OTPT [keep(ID_NAME, CHNG_FV)];
}

/*Takes as an input parameter a data set of structure {Identifier, Typ of protection} and returns a data set of structure {Identifier, Is residential collateral and financial guarantee, Is commercial collateral and financial guarantee}*/
define procedure EVLT_TYP_CLLTRL_GRNT(input INPT as dataset, input ID as string, output OTPT as dataset) {
INSTRMNT_IDS := INPT [keep(ID)];
/*Identification of residential collateral*/TMP := INPT [calc if (TYP_PRTCTN in ("8")) then "T" else "F" as "IS_RSTNTL_CLLTRL"];
/*Identification of commercial collateral*/TMP := TMP [calc if (TYP_PRTCTN in ("9", "10")) then "T" else "F" as "IS_CMMRCL_CLLTRL"];
/*Identification of financial guarantees*/TMP := TMP [calc if (TYP_PRTCTN in ("5", "41")) then "T" else "F" as "IS_FNNCL_GRNT"];
/*Extraction of a set of Identifiers that are related to residental collateral*/RSTNTL_CLLTRL := TMP [filter(IS_RSTNTL_CLLTRL = "T"), keep(ID)];
/*Extraction of a set of Identifiers that are related to commercial collateral*/CMMRCL_CLLTRL := TMP [filter(IS_CMMRCL_CLLTRL = "T"), keep(ID)];
/*Extraction of a set of Identifiers that*/FNNCL_GRNT := TMP [filter(IS_FNNCL_GRNT = "T"), keep(ID)];

/*Create a set of Identifiers that are related to real estate collateral and financial guarantees*/RSTNTL_CLLTRL_FNNCL_GRNT := intersect(RSTNTL_CLLTRL, FNNCL_GRNT);
RSTNTL_CLLTRL_FNNCL_GRNT := RSTNTL_CLLTRL_FNNCL_GRNT [calc "T" as "IS_RSTNTL_CLLTRL_FNNCL_GRNT"];
/*Create a set of Identifiers that are not related to real estate collateral and financial guarantees*/NT_RSTNTL_CLLTRL_FNNCL_GRNT := symdiff(RSTNTL_CLLTRL, FNNCL_GRNT);
NT_RSTNTL_CLLTRL_FNNCL_GRNT := NT_RSTNTL_CLLTRL_FNNCL_GRNT [calc "F" as "IS_RSTNTL_CLLTRL_FNNCL_GRNT"];
/*Create a set of structure {Identifier, Is residentifal collateral and financial guarantee}*/INSTRMNTS_RSTNTL_CLLTRL_FNNCL_GRNT := union(RSTNTL_CLLTRL_FNNCL_GRNT, NT_RSTNTL_CLLTRL_FNNCL_GRNT);

/*Create a set of Identifiers that are related to commercial collateral and financial guarantees*/CMMRCL_CLLTRL_FNNCL_GRNT := intersect(CMMRCL_CLLTRL, FNNCL_GRNT);
CMMRCL_CLLTRL_FNNCL_GRNT := CMMRCL_CLLTRL_FNNCL_GRNT [calc "T" as "IS_CMMRCL_CLLTRL_FNNCL_GRNT"];
/*Create a set of Identifiers that are not related to commercial collateral and financial guarantees*/NT_CMMRCL_CLLTRL_FNNCL_GRNT := symdiff(CMMRCL_CLLTRL, FNNCL_GRNT);
NT_CMMRCL_CLLTRL_FNNCL_GRNT := NT_CMMRCL_CLLTRL_FNNCL_GRNT [calc "F" as "IS_CMMRCL_CLLTRL_FNNCL_GRNT"];
/*Create a set of structure {Identifier, Is commercial collateral and financial guarantee}*/INSTRMNTS_CMMRCL_CLLTRL_FNNCL_GRNT := union(CMMRCL_CLLTRL_FNNCL_GRNT, NT_CMMRCL_CLLTRL_FNNCL_GRNT);

/*Create the data set that comprises the information if the protections related to an instrument may be considered as residential collateral and financial guarantee and / or commercial collateral and financial guarantee*/OTPT := [INSTRMNTS_RSTNTL_CLLTRL_FNNCL_GRNT, INSTRMNTS_CMMRCL_CLLTRL_FNNCL_GRNT on ID];
}

/*This procedure takes as an input a Counterparty identifier (CNTPRTY_ID) of an entity and returns as output a data set comprising the group structure of this entity in a flat 'hierarchy'. E.g. an entity A that comprises two subsidiaries B and C where C also contains two subsidiaries D and E the procedure returns a data set of structure {Group identifier, Legal entity identifier, other observations of the cube Group (GRP)} with content {A, {B, C, D, E}}. Due to the fact that VLT does not provide the functionality of iterations (in a straightforward manner) we are not able to express the algorithm that is necessary in order to derive this information. Therefore we indicate the output data set by only assigning the cube Group (GRP) to it. For the sake of completeness we describe the algorithm as follows: (1) select the records of the cube Group (GRP) where the Group identifier (GRP_ID) equals the input Counterparty identifier (CNTRPRTY_ID), (2) for each record extracted in (1) we extract again the records of the cube Group (GRP) where the Group identifier (GRP_ID) matches the input which is this time the Legal entity identifier (LGL_ENTTY_ID) that was extracted in (1). (3) We repeat this procedure until we do not receive any results (i.e. we arive at the leaf of the (group) tree structure). Every dataset that is extracted in (1) and (2) has the same structure and therefore we are able to union those datasets keeping in mind that we need to replace the Group identifier (GRP_ID) of each record by the initial (input) Counterparty identifier (CNTRPRTY_ID).*/
define procedure FLT_HRRCHY(input CNTRPRTY_ID as string, output OTPT as dataset) {
/*Due to the fact that VLT does not provide the functionality of iterations (in a straightforward manner) we are not able to express the algorithm that is necessary in order to derive this information. Therefore we indicate the output data set by only assigning the cube Group (GRP) to it. */
OTPT := GRP;
}

