/*map: (Is intragroup security, Is short position, Credit quality status) &rArr; Performing status (SHSG)*/
create function G_PRFRMNG_STTS1(IS_INTRGRP_SCRTY, IS_SHRT_PSTN, CRDT_QLTY_STTS) {
returns 
if (IS_INTRGRP_SCRTY = "T" or IS_SHRT_PSTN = "T") then "0" 
else 
   (if CRDT_QLTY_STTS in ("1", "2", "18", "19", "20") then "1" 
   elseif (CRDT_QLTY_STTS = "11") then "11" 
   else null) 
as string
}

/*map: (Assessment approach for credit quality status, Credit quality status, Impairment status, Credit quality status of the counterparty) &rArr; Performing status (AnaCredit)*/
create function G_PRFRMNG_STTS2 (APPRCH_CRDT_QLTY_STTS, CRDT_QLTY_STTS, IMPRMNT_STTS, CRDT_QLTY_STTS_CNTRPRTY) {
returns 
/*Transaction based approach*/if (APPRCH_CRDT_QLTY_STTS = "2") then 
   (/*Non-performing*/if CRDT_QLTY_STTS in ("2", "18", "19", "20") then "1" 
   /*Performing*/elseif CRDT_QLTY_STTS = "11" then "11" 
   else null) 
/*Debtor based approach*/elseif (APPRCH_CRDT_QLTY_STTS = "1") then  
   (/*Performing*/if (CRDT_QLTY_STTS_CNTRPRTY = "11" and IMPRMNT_STTS <> "25") then "11"  
   /*Non-performing*/elseif (CRDT_QLTY_STTS_CNTRPRTY in ("2", "18", "19", "20") or (CRDT_QLTY_STTS_CNTRPRTY = "11" and IMPRMNT_STTS = "25")) then "1"  
   else null) 
else null 
as string
}

