/*map: (Type of instrument, Is revolving loan, Is credit line other than revolving credit) &rArr; Type of instrument (AnaCredit)*/
create function G_TYP_INSTRMNT_AC (TYP_INSTRMNT, IS_RVLVNG_LN, IS_CRDT_LN_OTHR_RV_CRDT, INSTTTNL_SCTR) {
   returns 
   /*Current accounts*/if (TYP_INSTRMNT in ("260")) then
      /*Current accounts with MFIs are considered Deposits in AnaCredit*/if (INSTTTNL_SCTR in (Sectors03)) then "1000"
      /*otherwise such current accounts are considered as Overdrafts*/else "20"
   /*Factoring and Other trade receivables*/elseif (TYP_INSTRMNT in ("1020", "1023")) then 
       (if (IS_RVLVNG_LN = "T") then "1001" 
       elseif (IS_CRDT_LN_OTHR_RV_CRDT = "T") then "1002" 
       else "71")  
      /*Other loans*/elseif (TYP_INSTRMNT = "1022") then 
       (if (IS_RVLVNG_LN = "T") then "1001" 
       elseif (IS_CRDT_LN_OTHR_RV_CRDT = "T") then "1002" 
       else "1004") 
      /*for other types of instruments the input classification is equal to the classification required in AnaCredit*/else TYP_INSTRMNT
as string
}

/*map: (Relationship with securitisation or credit transfer, Type of risk transfer) &rArr; Type of securitisation (AnaCredit)*/
create function G_TYP_SCRTSTN1 (RLTNSHP_SCRTSTN_CRDT_TRNSFR, TYP_RSK_TRNSFR) {
   returns 
      if (RLTNSHP_SCRTSTN_CRDT_TRNSFR in ("0", "2")) then "7" 
      elseif (RLTNSHP_SCRTSTN_CRDT_TRNSFR = "1") then 
            (if (TYP_RSK_TRNSFR = "1") then "1" 
            elseif (TYP_RSK_TRNSFR = "6") then "6" 
/* cases different to traditional and synthetic securitisation */
            elseif (TYP_RSK_TRNSFR = "3") then "7" 
            else null) 
/* this case treats self-securitisation */ 
      elseif (RLTNSHP_SCRTSTN_CRDT_TRNSFR = "3") then "7" 
      else null 
as string
}

