define procedure PRCDR_CHCK_RLST (input TRGT as dataset, input RLST_ID as string, output RSLT as dataset) {
RSLT := check (TRGT, RLST_ID); 
RSLT := RSLT [drop (CONDITION)]; 
RSLT := RSLT [rename (RULE_ID as "VLDTN_ID")];
}

define procedure PRCDR_RFRNTL_INTGRTY (input FRGN_CB as dataset, input FRGN_VRBL as string, input PRMRY_CB as dataset, input PRMRY_VRBL as string, input VLDTN_ID as string, output RSLT as dataset) {
/*extract a set of Foreign variable (FRGN_VRBL), rename to ID, store in dataset Foreign identifiers (FRGN_IDS)*/FRGN_IDS := FRGN_CB [keep (FRGN_VRBL), rename (FGN_VRBL as "ID")]; 
/*extract a set of Primary variable (PRMRY_VRBL), rename to ID, store in dataset Primary identifiers (PRMRY_IDS)*/PRMRY_IDS := PRMRY_CB [keep (PRMRY_VRBL), rename (PRMRY_VRBL as "ID")]; 
/*calculate the set difference between Foreign identifiers (FRGN_IDS) and Primary identifiers (PRMRY_IDS)*/RSLT := setdiff (FRGN_IDS, PRMRY_IDS); 
/*rename ID to FRGN_IDS, make VLDTN_ID a measure variable*/RSLT := RSLT [rename (ID as "FRGN_IDS", VLDTN_ID role Measure)];
}
